import matplotlib.pyplot as pltimport numpy as npfrom mplsoccer import Pitch, Sbopenparser = Sbopen()df_competitions = parser.competition()df_matches = parser.match(competition_id=55, season_id=43)# Italy matches: [3788741, 3788754, 3788766, 3794685, 3795107, 3795220, 3795506]# Italy matches: [Turkey, England]df, related, freeze, tactics = parser.event(3788741)passes = df.loc[df['type_name'] == 'Pass'].loc[    df['sub_type_name'] != 'Throw-in'].set_index('id')# Jorginho name: Jorge Luiz Frello Filho# Jorgingo player_id: 7024mask_jorgi= (df.type_name == 'Pass') & (df.player_name ==                                         "Jorge Luiz Frello Filho")df_pass = df.loc[mask_jorgi, ['x', 'y', 'end_x', 'end_y']]pitch = Pitch(line_color='black')fig, ax = pitch.grid(grid_height=0.9, title_height=0.06, axis=False,                     endnote_height=0.04, title_space=0, endnote_space=0)fig.suptitle("Jorginho against Turkey", fontsize = 30)pitch.arrows(df_pass.x, df_pass.y,            df_pass.end_x, df_pass.end_y, color = "blue", ax=ax['pitch'])pitch.scatter(df_pass.x, df_pass.y, alpha = 0.2, s = 500,               color = "blue", ax=ax['pitch'])plt.show()